import { Component } from '@angular/core';
import { WorkoutSession } from '../models/workoutSession';
import { WorkoutService } from '../services/workout.service';
import { FormBuilder } from '@angular/forms';
import * as pdfMake from 'pdfmake/build/pdfmake';
import * as pdfFonts from 'pdfmake/build/vfs_fonts';

interface PDFContent {
  text: string;
}

@Component({
  selector: 'app-workout',
  templateUrl: './workout.component.html',
  styleUrls: ['./workout.component.scss']
})
export class WorkoutComponent {
  //npm install pdfmake --save
  //npm install @types/pdfmake --save-dev
  
  workouts: WorkoutSession[] = [];
  filteredWorkouts: WorkoutSession[] = [];
  search!: string;
  pdfMake: any;

  constructor(private workoutService: WorkoutService, private formBuilder: FormBuilder) {
    this.pdfMake = pdfMake;
    this.pdfMake.vfs = pdfFonts.pdfMake.vfs;
  }

  ngOnInit(): void {
    this.loadWorkouts();
  }

  loadWorkouts(): void {
    this.workoutService.getAllWorkout().subscribe(workouts => {
      this.workouts = workouts;
      // Initialize filteredExercises after exercices have been populated
      this.filteredWorkouts = this.workouts;
    })
  }

  deleteWorkout(id: number) {
    this.workoutService.deleteWorkout(id).subscribe(
      () => this.ngOnInit()
    )
  }

  searchWorkouts() {
    if (!this.search) {
      this.filteredWorkouts = this.workouts;
    } else {
      const searchTermLower = this.search.toLowerCase();
      this.filteredWorkouts = this.workouts.filter(e =>
        Object.values(e).some(val =>
          (typeof val === 'string' || typeof val === 'number') &&
          val.toString().toLowerCase().includes(searchTermLower)
        )
      );
    }
  }

  sortColumn: keyof WorkoutSession | null = null; // Initialize to an empty string
  sortDirection: 'asc' | 'desc' = 'asc'; // Initialize to ascending by default

  sort(property: keyof WorkoutSession): void {
    if (property === this.sortColumn) {
      // If the same column is clicked again, toggle the sorting direction
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      // If a different column is clicked, reset the sorting direction to ascending
      this.sortDirection = 'asc';
    }
  
    // Update the sortColumn to the clicked column
    this.sortColumn = property;
  
    this.filteredWorkouts.sort((a, b) => {
      const valA = a[property] as string | number;
      const valB = b[property] as string | number;
  
      if (typeof valA === 'string' && typeof valB === 'string') {
        return this.sortDirection === 'asc' ? valA.localeCompare(valB) : valB.localeCompare(valA);
      } else if (typeof valA === 'number' && typeof valB === 'number') {
        return this.sortDirection === 'asc' ? valA - valB : valB - valA;
      } else {
        return 0; // No sorting for other types
      }
    });
  }

  async generateWorkoutPDF(workout: WorkoutSession): Promise<void> {
    try {
        const exercises = await this.workoutService.getExercisesByWorkoutSessionId(workout.idWork).toPromise();

        const exercisesContent = exercises && exercises.length > 0
          ? exercises.map(exercise => `Name: ${exercise.nameExer}\nDesc: ${exercise.descriptionExe}\nDuration: ${exercise.durationExer} mins\n\n`).join('')
          : 'No exercises available for this workout.\n\n';

        const documentDefinition = {
          content: [
            { text: `Workout Details:\n\n`, style: 'header' },
            `Date: ${workout.date}\nDuration: ${workout.duration} mins\n\n`,
            { text: 'Exercises:\n\n', style: 'subheader' },
            exercisesContent
          ],
          styles: {
            header: {
              fontSize: 18,
              bold: true
            },
            subheader: {
              fontSize: 15,
              bold: true
            }
          },
          background: function(currentPage: any, pageSize: any) {
            const imageWidth = 800; // The width of your image

            const xPosition = pageSize.width / 2 - (imageWidth / 2); // This centers the image horizontally
            const yPosition = pageSize.height / 2 - (imageWidth / 2);
            return {
                image: 'data:image/png;base64,',
                width: imageWidth,
                absolutePosition: { x: xPosition, y: yPosition },
                opacity: 0.5 // Adjust opacity to make it look like a watermark
            };
        }
        };

        this.pdfMake.createPdf(documentDefinition).download(`workout-${workout.idWork}.pdf`);
    } catch (error) {
        console.error('Error fetching exercises for workout:', error);
    }
}



  
}
